import React, { Component } from 'react';
import Header from '../../components/header/index';
import { MetaTags } from 'react-meta-tags';
import Post from '../../components/post/index';
import PropTypes from 'prop-types';
import {Row, Col} from 'reactstrap';
import Container from './container';

class Blog extends Component {
  componentDidMount() {
    const { fetchPosts } = this.props;
    fetchPosts();
  }
  render() {
    const { posts } = this.props;

    return (
      <div>
        <h1>Recent Posts</h1>
        {posts.length === 0 ? (
          <div>
            <h2>No Posts loaded yet!</h2> <p>Be the first to add a post!</p>
          </div>
        ) : (
          <div>
              {posts.map(post => (
                  <Post post={post} />
              ))}
          </div>
        )}
      </div>
    );
  }
}

Blog.propTypes = {
  posts: PropTypes.arrayOf(PropTypes.object),
  fetchPosts: PropTypes.func.isRequired
};
Blog.defaultProps = {
  posts: [
    {
      id:'',
      title:'First Post',
      content:'Hello everyone and welcome to my blog! This blog serves the purposes of updating visitors to my portfolio site of my current projects and goals. Right now I am currently working on finishing up the final month of my degree program and am excited to being in my chosen industry! Thank you so much for taking this journey with me!',
    }
  ]
};


export default Container(Blog);
